def threeSumClosest(nums, target):

    nums.sort()
    res = float("inf")

    for i in range(len(nums)-2):

        l, r = i+1, len(nums)-1

        while l < r:
            
            s = nums[i] + nums[l] + nums[r]
            
            if abs(s - target) < abs(res - target):
                res = s
            
            if s > target:
                r -= 1
                
            else:
                l += 1

    return res

